const swiper=new Swiper(".swiper",{slidesPerView:1,spaceBetween:0,loop:!0,direction:"horizontal",effect:"fade",navigation:{nextEl:".swiper-button-next"}}),links=document.querySelectorAll(".nav__link");links.forEach(e=>{e.addEventListener("click",r=>{r.preventDefault(),document.getElementById(e.dataset.id).scrollIntoView({block:"start",behavior:"smooth"})})});const burgerIcon=document.querySelector(".header__burger"),burgerClose=document.querySelector(".burger__close"),burgerMenu=document.querySelector(".burger");burgerIcon.addEventListener("click",e=>{burgerMenu.classList.toggle("burger--active")}),burgerClose.addEventListener("click",e=>{burgerMenu.classList.remove("burger--active")});